package com.example.adapter;

import android.content.Context;
import android.view.LayoutInflater;
import android.view.View;
import android.view.ViewGroup;

import androidx.annotation.NonNull;
import androidx.recyclerview.widget.RecyclerView;

import com.bumptech.glide.Glide;
import com.example.kiki.R;
import com.example.kiki.databinding.CategoryLayoutBinding;
import com.google.android.gms.common.internal.ConnectionTelemetryConfiguration;

import java.util.ArrayList;

public class CategoryAdapter extends RecyclerView.Adapter<CategoryAdapter.ViewHolder> {
CategoryLayoutBinding bind;
    ArrayList<String> cList;
    Context context;

    public CategoryAdapter( Context context,ArrayList<String> cList) {
        this.cList = cList;
        this.context = context;
    }

    @NonNull
    @Override
    public ViewHolder onCreateViewHolder(@NonNull ViewGroup parent, int viewType) {
    bind = CategoryLayoutBinding.inflate(LayoutInflater.from(parent.getContext()),parent,false);
    return new ViewHolder(bind);
    }

    @Override
    public void onBindViewHolder(@NonNull ViewHolder holder, int position) {
        holder.binding.categoryName.setText("Clothing");
       Glide.with(context).load("data:image/jpeg;base64,").into(holder.binding.categoryImage);

    }

    @Override
    public int getItemCount() {
        return cList.size();
    }

    public class ViewHolder extends RecyclerView.ViewHolder {
        CategoryLayoutBinding binding;

        public ViewHolder(CategoryLayoutBinding b) {
            super(b.getRoot());
            binding = b;


        }
    }


}
